INSERT INTO `b_search_content_text` VALUES (9,'081e9fe0840fbb6c2ff01d5111b8f507','ПЕРСОНАЛЬНЫЙ РАЗДЕЛ\r\nВ ЛИЧНОМ КАБИНЕТЕ ВЫ МОЖЕТЕ ПРОВЕРИТЬ ТЕКУЩЕЕ СОСТОЯНИЕ КОРЗИНЫ, ХОД ВЫПОЛНЕНИЯ ВАШИХ ЗАКАЗОВ, ПРОСМОТРЕТЬ ИЛИ ИЗМЕНИТЬ ЛИЧНУЮ ИНФОРМАЦИЮ, А ТАКЖЕ ПОДПИСАТЬСЯ НА НОВОСТИ И ДРУГИЕ ИНФОРМАЦИОННЫЕ РАССЫЛКИ. \rЛИЧНАЯ ИНФОРМАЦИЯ\rИЗМЕНИТЬ РЕГИСТРАЦИОННЫЕ ДАННЫЕ\rЗАКАЗЫ\rОЗНАКОМИТЬСЯ С СОСТОЯНИЕМ ЗАКАЗОВ\rПОСМОТРЕТЬ СОДЕРЖИМОЕ КОРЗИНЫ\rПОСМОТРЕТЬ ИСТОРИЮ ЗАКАЗОВ\r\n');
